--
-- Структура таблицы `order`
--

CREATE TABLE IF NOT EXISTS `order` (
  `id_order` int(11) NOT NULL AUTO_INCREMENT,
  `tourist_email` varchar(255) CHARACTER SET utf8 NOT NULL,
  `tourist_name` varchar(255) CHARACTER SET utf8 NOT NULL,
  `name_tour` varchar(255) CHARACTER SET utf8 NOT NULL,
  `name_country` varchar(255) CHARACTER SET utf8 NOT NULL,
  `name_hotel` varchar(255) CHARACTER SET utf8 NOT NULL,
  `cost` int(11) NOT NULL,
  `date` int(11) NOT NULL,
  PRIMARY KEY (`id_order`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8mb4 AUTO_INCREMENT=54 ;

--
-- Дамп данных таблицы `order`
--

