--
-- Структура таблицы `tour`
--

CREATE TABLE IF NOT EXISTS `tour` (
  `id_tour` int(11) NOT NULL AUTO_INCREMENT,
  `name_tour` varchar(255) NOT NULL,
  `short_text` varchar(255) NOT NULL,
  `text` text NOT NULL,
  `tour_cost` int(11) NOT NULL,
  `id_country` int(11) NOT NULL,
  `visible` int(11) NOT NULL,
  `image` text NOT NULL,
  PRIMARY KEY (`id_tour`),
  KEY `id_country` (`id_country`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=5 ;

--
-- Дамп данных таблицы `tour`
--

INSERT INTO `tour` (`id_tour`, `name_tour`, `short_text`, `text`, `tour_cost`, `id_country`, `visible`, `image`) VALUES
(3, 'Хургада', 'Солнечная Хургада - самый крупный из всех курортов в Египте. ', '<p style="text-align: justify;">\r\n	<span style="font-size:16px;"><span style="color: rgb(54, 54, 54); font-family: Tahoma, Arial, Verdana, sans-serif;">Солнечная Хургада - самый крупный из всех курортов в Египте. Яркое солнце и теплое море, великолепие коралловых рифов, восточные танцы - праздник, который ждет вас в Хургаде круглый год.</span><br style="color: rgb(54, 54, 54); font-family: Tahoma, Arial, Verdana, sans-serif; font-size: 11px;" />\r\n	<span style="color: rgb(54, 54, 54); font-family: Tahoma, Arial, Verdana, sans-serif;">Благодаря низкой влажности и постоянному бризу с моря, даже при высокой температуре воздуха жара переносится очень легко и полуденный зной не изнуряет. Климат благоприятен для всех возрастов без исключения.</span></span></p>\r\n', 25000, 1, 1, 'hyrghada1.jpg'),
(4, 'Анталия', 'Анталийское побрежье стало настолько родным и любимым для российских туристов, ', '<p style="text-align: justify;">\r\n	<span style="font-size:16px;"><span style="color: rgb(54, 54, 54); font-family: Tahoma, Arial, Verdana, sans-serif;">Анталийское побрежье стало настолько родным и любимым для российских туристов, что его даже в шутку называют &laquo;турецкой дачей&raquo;. Сама Анталия дачу напоминает мало &ndash; это крупный современный город с двухмиллионным населением, деловой и торговый центр побережья, с отличным суперсовременным аэропортом. Но еще сорок лет назад, до введения беспошлинной зоны и последующего за ним строительного и бизнес-бума, Анталия была лишь милым провинциальным городком с уходящей вглубь веков историей. Веселая открытая Анталия неизменно привлекает людей с живым характером, настроенных получить максимум удовольствия на отдыхе. А мягкий микроклимат закрытой бухты Анталии, безветрие и ярко-синее море делают отпуск просто сказочным!</span></span></p>\r\n', 99999, 2, 1, 'antalya3.jpg');

